import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css'
import { Children } from 'react';


const pizzaData = [
    {
      name: "Focaccia",
      ingredients: "Bread with italian olive oil and rosemary",
      price: 6,
      photoName: "pizzas/focaccia.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Margherita",
      ingredients: "Tomato and mozarella",
      price: 10,
      photoName: "pizzas/margherita.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Spinaci",
      ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
      price: 12,
      photoName: "pizzas/spinaci.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Funghi",
      ingredients: "Tomato, mozarella, mushrooms, and onion",
      price: 12,
      photoName: "pizzas/funghi.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Salamino",
      ingredients: "Tomato, mozarella, and pepperoni",
      price: 15,
      photoName: "pizzas/salamino.jpg",
      soldOut: true,
    },
    {
      name: "Pizza Prosciutto",
      ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
      price: 18,
      photoName: "pizzas/prosciutto.jpg",
      soldOut: false,
    },
  ];


function App() {
    return <div className='container'>
        <Header />
        <Menu />
        <Footer />
    </div>
}


function Header(){
    // const headerStyle = {color:'pink', fontSize: "4rem"};

    return (
        <header className='header'>
            <h1 >First Pizaa Menu Co.</h1>
        </header>
    )
}

function Menu() {

    const pizzas = pizzaData;

    const pizzaNum = pizzas

    return (

        <main className='menu' style={{float:"left"}}>
            <h2>Our menu</h2>
            {/* 在这里pizza作为pizzaData中的item,作为模板,来构建列表 */}
            {/* 为pizza数组中的每一个对象渲染一个披萨🍕元素 */}
            {/* 再次通过 && 进行判断,如果数组里有值,那么就渲染 */}
            {/* 下面的判断中,尽量不使用值类型,而是使用一个布尔值来控制开和关 */}
            {pizzaNum > 0 && (
                <ul className='pizzas'>
                    {pizzas.map((pizza) => (
                    <Pizza pizzaObj={pizza} key={Pizza.name} />
                    ))}
                </ul>
            )}

        </main>
    )
}

// 函数必须大写开头
function Pizza(props) {
    return (
    <li className="pizza">
        <img src={props.pizzaObj.photoName} alt={props.pizzaObj.Name}></img>
        <div>
            <h3>{props.pizzaObj.name}</h3>
            <p>{props.pizzaObj.ingredients}</p>
            <span>Price:{props.pizzaObj.price + 5}</span>
        </div>
    </li>
    )
}

function Footer() {

    const hour = new Date().getHours();
    const openHour = 9;
    const closeHour = 22;
    const isOpen = hour >= openHour && hour <= closeHour;

    // if(isOpen) alert("We are currently open!");else alert("We are closed");
    return (
    <footer>
        {/* && 运算符,在这里可以起到判断的作用,用于取代 if 语句 */}
        {isOpen && (
            <div className='order'>
                <p>
                    It's {new Date().toLocaleTimeString().slice(0,-3)} now,We are open until 22:00,currently open!
                </p>
                <button className='btn'>Order</button>
            </div>

            )}
    </footer>
    );
}

// React Version 18
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
    <React.StrictMode>
        <App />
    </React.StrictMode>
);
