import { useState } from "react";

export default function Form({ onAddItems }) {

	const [description, setDescription] = useState("")
	const [quantity, setQuantity] = useState(1)

	function handleSubmit(e) {
		e.preventDefault()
		//ÁÇπÂáªÊèê‰∫§‰πãÂêé,ÂàõÂª∫ListÁöÑitem

		//Â¶ÇÊûúdescription‰∏∫Á©∫,ÈÇ£‰πàÂ∞±‰∏çËÉΩÊèê‰∫§
		if (!description) return;

		const newItem = {
			description, quantity, packed: false, id: Date.now()
		};
		console.log(newItem);

		onAddItems(newItem);

		//Êèê‰∫§‰πãÂêéÂ∞ÜÁä∂ÊÄÅÊÅ¢Â§çÂà∞ÂàùÂßãÂÄº
		setDescription("");
		setQuantity(1);
	}

	return (
		// onSubmitÁöÑÊñπÊ≥ïÊ∑ªÂä†Âú®form‰∏äÈù¢,‰∏çÁõ¥Êé•Ë∞ÉÁî®ÊñπÊ≥ï
		<form className='add-form' onSubmit={handleSubmit}>
			<h3>what do you need for your trip?ü•∞</h3>
			<select value={quantity} onChange={(e) => setQuantity(Number(e.target.value))}>
				{/* ËøôÊÆµÁöÑ‰∏ªË¶ÅÁõÆÁöÑÊòØÂú®selected‰∏≠ÁîüÊàê20‰∏™option */}
				{/* Á¨¨‰∏ÄÊ≠•: Array‰∏Ä‰∏™Êï∞ÁªÑ,ÈáåËæπÊúâ20‰∏™Êï∞Â≠ó,ÈÄöËøá (_, i) => i + 1 Èó≠ÂåÖÁªôÁªô‰∏™Êï∞Â≠óÈÄíÂ¢ûËµãÂÄº */}
				{/* Á¨¨‰∫åÊ≠•: ‰ΩøÁî®mapÊñπÊ≥ï‰ΩøÁî®array‰∏≠ÁöÑnumÁîüÊàêoption,optionÁöÑvalueÂíåkeyÈÉΩÊòØnum */}

				{Array.from({ length: 20 }, (_, i) => i + 1).map
					(num => (
						<option value={num} key={num} >
							{num}
						</option>
					))}
			</select>
			<input
				type='text'
				placeholder="Item..."
				value={description}

				// Â∞ÜÂèëÁîü‰∫ÜÂèòÂåñÁöÑ‰∏úË•ø,‰πüÂ∞±ÊòØËæìÂÖ•ÂÄº,‰º†ÂÖ•ÁöÑevent,‰øÆÊîπevent.target.‰∏≠ÁöÑvalue
				// ËøôÊ†∑:ËæìÂÖ•ÁöÑÂÜÖÂÆπÂÆåÂÖ®ÂíådescriptionÊòØÂêåÊ≠•ÁöÑ
				onChange={(e) => {
					setDescription(e.target.value)
					console.log(e.target.value);
				}
				}
			/>
			<button type='submit'>Add</button>


			{/* ‰∏ãÈù¢ÁöÑinputÈÖçÂêàhandleSubmit,ÂêåÊ†∑ÂèØ‰ª•ÂÆûÁé∞ÊåâÈíÆÁöÑÂäüËÉΩ,Âπ∂‰∏îÊòØ‰∏Ä‰∏™Áä∂ÊÄÅ, ËÄåÂ¶ÇÊûúÊòØsubmit,Âè™ÊúâÁÇπÁöÑ‰∏ÄÁû¨Èó¥Êâç‰ºöÂá∫ÂèëÊèê‰∫§ÂáΩÊï∞ */}
			{/* <input onClick={handleSubmit} type='button'  value="add"/> */}
		</form>
	);
}
