import { Children, useState } from 'react';

const messages = [
	"Learn React ‚öõÔ∏è",
	"Apply For Jobs üíº",
	"Invest your new income ü§ë"
]


export default function App() {
	const [step, setStep] = useState(1)
	const [score, setScore] = useState(1)
	const [IsOpen, setIsOpen] = useState(false)
	// ‰ΩøÁî®use‰Ωú‰∏∫ÂâçÁºÄÁöÑÂáΩÊï∞ Áß∞‰∏∫Èí©Â≠êÂáΩÊï∞ Hooks
	// HooksÂè™ËÉΩÂú®Â§ñÂ±Ç‰ΩøÁî®,‰∏çËÉΩÂú®ifËØ≠Âè•ÊàñËÄÖÂÖ∂‰ªñÂæ™ÁéØ‰∏≠‰ΩøÁî®

	function handlePrevious() {
		if (step > 1) {
			setStep(step - 1);
		}
	}
	function handleNext() {
		if (step < 3) {
			setStep(step + 1);
		}
	}
	// --- UIÈÉ®ÂàÜ ---
	return <>
		<button style={{ backgroundColor: "#7950f2", color: "#fff", padding: "6px", borderRadius: "5px", fontWeight: "bold" }}
			// ËøôÈáåÁõ¥Êé•Ë∞ÉÁî®ÊñπÊ≥ï
			onClick={() => setIsOpen(!IsOpen)}
		>
			Toggle
		</button>
		{IsOpen && (
			<div className="steps">
				<div className="numbers">
					{/* Ê®°ÊùøÂ≠êÁ¨¶‰∏≤ÁöÑ‰ΩøÁî®ÊñπÂºè --- `${}` --- */}
					{/* ‰ΩøÁî®useState,Â∞±‰∏çÈúÄË¶ÅÊ®°ÊùøÂ≠óÁ¨¶‰∏≤,Âõ†‰∏∫Âπ∂Ê≤°ÊúâÂàõÂª∫Â≠óÁ¨¶‰∏≤ */}
					<div className={step >= 1 ? "active" : ""}>1</div>
					<div className={step >= 2 ? "active" : ""}>2</div>
					<div className={step >= 3 ? "active" : ""}>3</div>
				</div>


				<StepMessage step={step}>
					{messages[step - 1]}
					<Button></Button>
				</StepMessage>

				<div className="buttons">
					<Button 
						bgColor="#7950f2" 
						textColor="#fff" 
						onClick={handlePrevious}>
						<span>ü•≥</span>
						Previous
					</Button>

					<Button 
						bgColor="#7950f2" 
						textColor="#fff" 
						onClick={handleNext}>
						<span>üëâüèº</span>
						<span>ü•≥</span>
						Next
					</Button>
				</div>
			</div>)
		}
	</>
	// --- UIÈÉ®ÂàÜ ---
}

function Button({textColor,bgColor,onClick,children}){
	return (
		<button style={{ backgroundColor: bgColor, color: textColor }}
			// ËøôÈáåÂè™Ë∞ÉÁî®ÂáΩÊï∞ÂêçÂ≠ó,ËÄå‰∏çÁõ¥Êé•Ë∞ÉÁî®ÊñπÊ≥ï
			onClick={onClick}>
			{children}
		</button>
	)
}

function StepMessage({step,children}){
	return (
		<p className="message">
			<h3>Step:{step}</h3>
			{children}
		</p>
	)
}
