import { useEffect } from "react";

export function useKey(key,action) {
    // 检测按键
    useEffect(function () {
        const Escape = (e) => {
            if (e.code === key) {
                action();
                // onCloseMovie();
                console.log('console');
            }
        }

        document.addEventListener('keydown', Escape)
        // function cleanUp
        return function () {
            document.removeEventListener('keydown', Escape)
        }
    }, [onCloseMovie]);

}
