import { useState } from "react";
import ProTypes from "prop-types"

const containerStyle = {
  display: "flex",
  alignmentItem: "center",
  gap: "16px"
};

const starContaqinerStyle = {
  display: "flex",
};



// =============  App  =============

const StartRating = ({ 
    maxRating = 5 ,
    color = '#fcc419',
    size=48,
    className="",
    messages=[],
    onSetRating,
  }) => {
  const [rating, setRating] = useState(0)
  const [tempRating,setTempRating] = useState(0);
  // const [movieRating,setMovieRating] = useState(0);

  function handleRating(rating) {
    setRating(rating);
    onSetRating(rating);
  }

  const textStyle = {
    lineHeight: `${size}px`,
    verticalAlign:"center",
    margin: "0",
    color:`${color}`,
    fontWeight:"bold",
    fontSize:`${size/2}px`
  };

  return (
    <div style={containerStyle}>
      <div style={starContaqinerStyle}>
        {Array.from({ length: maxRating }, (_, i) => (
          <Star 
            key={i} 
            onRate={() => (handleRating(i + 1))} 
            full={tempRating ? tempRating >= i + 1 : rating >= i + 1}
            onHoverIn={()=> setTempRating(i+1)}
            onHoverOut={()=> setTempRating(0)}
            color={color}
            size={size}
          />
        ))}
      </div>
      {/* <p style={textStyle}>
        {messages.length === maxRating ? 
          (messages[tempRating ? 
            tempRating-1 : 
            rating - 1] : 
          tempRating || rating || ""}
      </p> */}
    </div>
  )
}

export default StartRating


StartRating.prototype = {
  maxRating:ProTypes.number,
  color:ProTypes.string,
  size:ProTypes.number,
  messages:ProTypes.array,
}

function Star({ onRate, full, onHoverIn, onHoverOut ,color,size=48}) {
  const starStyle = {
    width: `${size}px`,
    height: `${size}px`,
    display: "block",
    cursor: "pointer",
    color:`${color}`,
  }
  return (
    <span 
      style={starStyle} 
      onClick={onRate} 
      onMouseEnter={onHoverIn}
      onMouseLeave={onHoverOut}
    >
    {full ? <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 20 20"
      fill={color}
      stroke={color}
    >
      <path
        d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"
      />
    </svg>
     : 
    <svg
      xmlns="http://www.w3.org/2000/svg"
      fill="none"
      viewBox="0 0 24 24"
      stroke={color}
    >
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth="{2}"
        d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118l-3.976-2.888c-.784-.57-.38-1.81.588-1.81h4.914a1 1 0 00.951-.69l1.519-4.674z"
      />
    </svg>}
  </span>
  )

}

