import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css'
import { Children } from 'react';



const pizzaData = [
    {
      name: "Focaccia",
      ingredients: "Bread with italian olive oil and rosemary",
      price: 6,
      photoName: "pizzas/focaccia.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Margherita",
      ingredients: "Tomato and mozarella",
      price: 10,
      photoName: "pizzas/margherita.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Spinaci",
      ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
      price: 12,
      photoName: "pizzas/spinaci.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Funghi",
      ingredients: "Tomato, mozarella, mushrooms, and onion",
      price: 12,
      photoName: "pizzas/funghi.jpg",
      soldOut: true,
    },
    {
      name: "Pizza Salamino",
      ingredients: "Tomato, mozarella, and pepperoni",
      price: 15,
      photoName: "pizzas/salamino.jpg",
      soldOut: true,
    },
    {
      name: "Pizza Prosciutto",
      ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
      price: 18,
      photoName: "pizzas/prosciutto.jpg",
      soldOut: false,
    },
  ];


function App() {
    return <div className='container'>
        <Header />
        <Menu />
        <Footer />
    </div>
}


function Header(){
    // const headerStyle = {color:'pink', fontSize: "4rem"};

    return (
        <header className='header'>
            <h1 >First Pizaa Menu Co.</h1>
        </header>
    )
}

function Menu() {

    const pizzas = pizzaData;

    const pizzaNum = pizzas.length

    return (

        <main className='menu' style={{float:"left"}}>
            <h2>Our menu</h2>
            {/* 在这里pizza作为pizzaData中的item,作为模板,来构建列表 */}
            {/* 为pizza数组中的每一个对象渲染一个披萨🍕元素 */}
            {/* 再次通过 && 进行判断,如果数组里有值,那么就渲染 */}
            {/* 下面的判断中,尽量不使用值类型,而是使用一个布尔值来控制开和关 */}

            {/* \在js模式,不能使用js语句的原因是我们需要一些能产生值的东西,比如Bool,而js语句的if判断是不能产生值的 */}

            {/* 下面之所以能够重构props,是因为可以确定{ 属性 } 就是props,这样可以少写一步props*/}

            <p>
                在js模式,不能使用js语句的原因是我们需要一些能产生值的东西,比如Bool,
                而js语句的if判断是不能产生值的,下面之所以能够重构props,是因为可以
                确定属性就是props,这样可以少写一步props
            </p>

            {pizzaNum > 0 ? (
                <ul className='pizzas'>
                    {pizzas.map((pizza) => (
                    <Pizza pizzaObj={pizza} key={Pizza.name} />
                    ))}
                </ul>
            ) : (
                <p>We are still work on our menu,please go back later:)</p>
            )}

        </main>
    )
}

// 函数必须大写开头
// 使用重构来使代码更简便,用 {pizzaObj} 来代替props
// 这样写的依据是,直接传入道具名称, {pizzaObj} 可能是props的一部分
function Pizza({pizzaObj}) {

    if(pizzaObj.soldOut) return (
        <li className="pizza">
        <img src={pizzaObj.photoName} alt={pizzaObj.Name}></img>
        <div>
            <h3>{pizzaObj.name} was soldout!</h3>
        </div>
    </li>
    )

    return (
    <li className="pizza">
        <img src={pizzaObj.photoName} alt={pizzaObj.Name}></img>
        <div>
            <h3>{pizzaObj.name}</h3>
            <p>{pizzaObj.ingredients}</p>
            <span>Price:{pizzaObj.price + 5}</span>
        </div>
    </li>
    )
}

function Footer() {

    const hour = new Date().getHours();
    const openHour = 9;
    const closeHour = 23;
    const isOpen = hour >= openHour && hour <= closeHour;

    // if(isOpen) alert("We are currently open!");else alert("We are closed");
    return (
    <footer>
        {/* && 运算符,在这里可以起到判断的作用,用于取代 if 语句 */}
        {isOpen && (
                <Order closeHour={closeHour}/>
            )}
    </footer>
    );
}

function Order({closeHour}){
    return(
        <div className='order'>
        <p>
            It's {new Date().toLocaleTimeString().slice(0,-3)} now,We are open until {closeHour}:00,currently open!
        </p>
        <p>We are looking fordward to your walcome!</p>
        <button className='btn'>Order</button>
    </div>
    )
}
// React Version 18
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
    <React.StrictMode>
        <App />
    </React.StrictMode>
);
