import './App.css';
import { useState } from 'react'
import Logo from './Logo.js'
import Form from './Form.js'
import {PackingList} from './PackingList.js';
import {Stats} from './Stats.js';

// 初始的initialItems硬编码,使用在App中定义的State items替代

// const initialItems = [
// 	{ id: 1, description: "Pasport", quantity: 2, packed: false },
// 	{ id: 2, description: "Socks", quantity: 12, packed: false },
// 	{ id: 3, description: "charge", quantity: 1, packed: true },
// ]

function App() {
	const [items, setItems] = useState([])
	//将items的长度给另外一个变量，他就会随着重新渲染自动变化，不需要手动更新 

	function handleAddItems(item) {
		//react 是不变的,所以我们不能直接修改数组,而是创建一个新的数组,--- 使用数组解构的 Rest符
		setItems(items => [...items, item]);
		setItems((num)=> num + 1) 
	}

	function handleDeleteItem(id){
		// 取出所有id不为现在这个值的item
		setItems(items => items.filter(item=>item.id !== id))
	}

	function handleToggleItem(id) {
		setItems(items => items.map(
			(item) => item.id === id ? {...item,packed: !item.packed} : item)
		);
	}

	function handleClearItem(){
		const confirmed = window.confirm("Are you sure you want to delete all items?");
		if(confirmed) setItems([]);
	}



	//--------- UI界面 -----------

	return (
		<div className="App">
			<Logo />
			{/* 这里的onAddItem是自定义的方法,在Form中使用onAddItem
				相当于使用上层App组件的handleAddItem */}
			<Form onAddItems={handleAddItems} />
			<PackingList items={items} 
				onDeleteItem = {handleDeleteItem} 
				onToggleItems={handleToggleItem} 
				onClearItem={handleClearItem}
			/>
			<Stats items={items}/>
		</div>
	);

	//--------- UI界面 -----------
}





export default App;
