export function Stats({ items }) {
	if (!items.length) {
		return (
			<p className='stats'>
				<em>Start add some item to your packing list</em>
			</p>
		);
	}
	const numItem = items.length;
	const numPacked = items.filter((item) => item.packed).length;
	const percetage = Math.round((numPacked / numItem) * 100);
	return (
		<footer className='stats'>
			<em>{percetage === 100 ?
				"You have get everything! ready to go" :
				`You have ${numItem} items on your list,and you already packed ${numPacked} (${percetage}%)`}
			</em>
		</footer>
	);
}
