import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css'
import { Children } from 'react';


const pizzaData = [
    {
      name: "Focaccia",
      ingredients: "Bread with italian olive oil and rosemary",
      price: 6,
      photoName: "pizzas/focaccia.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Margherita",
      ingredients: "Tomato and mozarella",
      price: 10,
      photoName: "pizzas/margherita.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Spinaci",
      ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
      price: 12,
      photoName: "pizzas/spinaci.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Funghi",
      ingredients: "Tomato, mozarella, mushrooms, and onion",
      price: 12,
      photoName: "pizzas/funghi.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Salamino",
      ingredients: "Tomato, mozarella, and pepperoni",
      price: 15,
      photoName: "pizzas/salamino.jpg",
      soldOut: true,
    },
    {
      name: "Pizza Prosciutto",
      ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
      price: 18,
      photoName: "pizzas/prosciutto.jpg",
      soldOut: false,
    },
  ];


function App() {
    return <div className='container'>
        <Header />
        <Menu />
        <createPizza />
        <Footer />
    </div>
}


function Header(){
    // const headerStyle = {color:'pink', fontSize: "4rem"};

    return (
        <header className='header'>
            <h1 >First Pizaa Menu Co.</h1>
        </header>
    )
}

function createPizza({pizzaData}){
    const allPizza = []
    Children.forEach(pizzaData,(child,index) =>{
        const example = <Pizza 
                name={child[index].name}
                photoName={child[index].photoName}
                ingredients={child[index].ingredients}
                price={10 + index}
        />
        allPizza.push(example)
    });
}
function Menu() {
    return (

        <main className='menu'>
            <h2>Our menu</h2>

            {/* <Pizza 
                name="Good Pizza"
                photoName="./pizzas/spinaci.jpg"
                ingredients="Tomato, mozarella, spinach, and ricotta cheese"
                price={10}
            />

            <Pizza 
                name="Pizza Funghi"
                photoName="./pizzas/funghi.jpg"
                ingredients="Tomato, mozarella, mushrooms, and onion"
                price={20}
            />
            <Pizza 
                name="Good Pizza"
                photoName="./pizzas/spinaci.jpg"
                ingredients="Tomato, mozarella, spinach, and ricotta cheese"
                price={10}
            /> */}




        </main>
    )
}

// 函数必须大写开头
function Pizza(props) {
    return (
    <div className='pizza'>
        <img src={props.photoName} alt='spinaci'></img>
        <div>
            <h3>{props.name}</h3>
            <p>{props.ingredients}</p>
            <span>Price:{props.price + 5}</span>
        </div>
    </div>
    )
}

function Footer() {

    const hour = new Date().getHours();
    const openHour = 9;
    const closeHour = 22;
    const isOpen = hour >= openHour && hour <= closeHour;

    // if(hour >= openHour && hour <= closeHour) alert("We are currently open!")
    // else alert("We are closed")

    return <footer>It's {new Date().toLocaleTimeString()} now,We are currently open!</footer>
}




// React Version 18
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
    <React.StrictMode>
        <App />
    </React.StrictMode>
);
