import { Children, useEffect, useRef, useState } from "react";
import StartRating from "./StartRating.js"
import { useMovies } from "./useMovies"
import { useLocalStorageState } from "./useLocalStorageState.js";
import { useKey } from "./useKey.js";


const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);

//=========  App  ==========
const KEY = `626f89cc`;

export default function App() {
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState(null);
  // ÂéüÊù•Ëøô‰∏â‰∏™ÂèòÈáèÊòØApp‰∏≠ÁöÑState,ËΩ¨ÁßªÂà∞useMovies‰∏≠‰πãÂêé,‰ªéhook‰∏≠ÂØºÂá∫,ÂºïÂÖ•Âà∞ËøôÈáå
  const { movies,isLoading,error } = useMovies(query);

  const [watched,setWatched] = useLocalStorageState([],"watched");

  function handleSelectMovie(id) {
    setSelectedId((selectedId) => (id === selectedId ? null : id));
  }

  function handleCloseMovie(id) {
    setSelectedId(null);
  }

  function handleAddWatch(movie) {
    setWatched((watched) => [...watched, movie]);
  }

  function handleDeleteWatched(id){
    //Èô§‰∫ÜidÁõ∏ÂêåÁöÑÈÇ£‰∏™,ÂÖ∂‰ªñÁöÑÂÖ®ÈÉ®ËøáÊª§‰∏Ä‰∏ã,Áõ∏ÂΩì‰∫éÂà†Èô§Êéâ‰∫Ü‰∏Ä‰∏™
    setWatched(watched=>watched.filter(movie=>movie.imdbID !== id));
  }
  // Êñ∞ÂÜÖÂÆπ
  // Ê∑ªÂä†Êú¨Âú∞Â≠òÂÇ®
  // useEffect(function(){
  //   // Ê∑ªÂä†Êú¨Âú∞Â≠òÂÇ®
  //   localStorage.setItem('watched',JSON.stringify(watched))
  // },[watched])

  return (
    <>
      <NavBar>
        <Search query={query} setQuery={setQuery} />
        <NumResults movies={movies} />
      </NavBar>
      <Main>
        <Box>
          {isLoading && <Loader />}
          {!isLoading && !error && <MoviesList movies={movies} onSelectMovie={handleSelectMovie} />}
          {error && <Error message={error} />}

        </Box>
        <Box>
          {selectedId ? (
            <MovieDetails
              selectedId={selectedId}
              onCloseMovie={handleCloseMovie}
              onAddWatched={handleAddWatch}
              watched={watched}
            />
          ) : (
            <>
              <WatchedSummary watched={watched} />
              <WatchedMoviesList 
                watched={watched}
                onDeleteWatched = {handleDeleteWatched}  
              />
            </>
          )}
        </Box>
      </Main>
    </>
  );
}

//=========  App  ==========

function Loader() {
  return <p className="loader">Loading...</p>
}

function Error({ message }) {
  return <p className="error"><span>‚õîÔ∏è</span>{message}</p>
}

// =========  NavBar  =======
function NavBar({ children }) {

  return <nav className="nav-bar">
    <Logo />
    {children}
  </nav>
}

function Search({ query, setQuery }) {

  const inputEl =  useRef(null)

  useKey("Enter",()=>{
    if(document.activeElement === inputEl.current)
      return;

    inputEl.current.focus();
    setQuery("");
  });

  return (
    <input
      className="search"
      type="text"
      placeholder="Search movies..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
      ref={inputEl}
    />
  )
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">üçø</span>
      <h1>usePopcorn</h1>
    </div>
  )
}

function NumResults({ movies }) {
  return (
    <p className="num-results">
      Found <strong>{movies.length}</strong> results
      {/* Found <strong>X</strong> results */}
    </p>
  )
}

// ===========  NavBar  ==========

// ===========  Main  ==========
function Main({ children }) {
  return (
    <main className="main">{children}</main>
  )
}

function Box({ children }) {

  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button
        className="btn-toggle"
        onClick={() => setIsOpen((open) => !open)}
      >
        {isOpen ? "‚Äì" : "+"}
      </button>
      {isOpen && (children)}
    </div>
  )
}

// function

function MoviesList({ movies, onSelectMovie }) {

  return (
    <ul className="list list-movies">
      {movies?.map((movie) => (
        <Movie movie={movie} key={movie.imdbID} onSelectMovie={onSelectMovie} />
      ))}
    </ul>
  )
}

function Movie({ movie, onSelectMovie, onCloseMovie }) {
  return (
    <li onClick={() => onSelectMovie(movie.imdbID)}>
      <img src={movie.Poster} alt={`${movie.Title} poster`} />
      <h3>{movie.Title}</h3>
      <div>
        <p>
          <span>üóì</span>
          <span>{movie.Year}</span>
        </p>
      </div>
    </li>
  )
}

function MovieDetails({ selectedId, onCloseMovie, onAddWatched, watched }) {
  const [movie, setMovie] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [userRating, setUserRating] = useState(null);

  const isWatched = watched.map(movie => movie.imdbID).includes(selectedId)
  const watchedMovieRate = watched.find((movie) => movie.imdbID === selectedId)?.userRating
  // console.log(`ÁúãËøá: ${isWatched}`);
  // console.log(haveID);

  const countRef = useRef(0);

  useEffect(function(){
    if(userRating) countRef.current += 1
    console.log(countRef.current);
  },[userRating])

  const {
    Title: title,
    Year: year,
    Poster: poster,
    Runtime: runtime,
    Actors: actors,
    Director: director,
    Genre: genre,
    Released: released,
    imdbRating: imdbRating,
    Plot: plot
  } = movie;
  // console.log(title, year, actors, String(imdbRating));

  const [avgRating,setAvgRating] = useState(0);

  function handleAdd() {
    const newWatchedMovie = {
      imdbID: selectedId,
      title,
      year,
      poster,
      imdbRating: Number(imdbRating),
      runtime: Number(runtime.split(" ").at(0)),
      userRating,
      countRatingDecisions:countRef.current
    };
    onAddWatched(newWatchedMovie);
    onCloseMovie();

    // setAvgRating(Number(imdbRating));
    // setAvgRating((avgRating)=>(avgRating + userRating) / 2);
  }
  useKey('Escape',onCloseMovie);

  // ÊêúÁ¥¢ËæìÂÖ•Ê°Ü‰∏≠ÁöÑÂÜÖÂÆπ
  useEffect(function () {
    async function getMovieDetails() {
      setIsLoading(true);
      const res = await fetch(`https://www.omdbapi.com/?apikey=${KEY}&i=${selectedId}`)
      const data = await res.json()
      console.log(data);
      setMovie(data);
      setIsLoading(false);
    };
    getMovieDetails();
  }, [selectedId])

  // Â∞ÜÊ†áÈ¢òÊîπÊàêÁîµÂΩ±ÂêçÂ≠ó
  useEffect(function(){

    if (!title) return ;
    document.title = `Movie | ${title}`;
    return function(){
      document.title = "usePopcorn";
    }
  },[title])

  return (
    <>
      <div className="details">
        {isLoading ? <Loader /> :
          <>
            <header className="header">
              <button
                className="btn-back"
                onClick={() => onCloseMovie()}
              >
                &larr;
              </button>
              <img src={poster} alt={`Poster of ${movie} movie`}></img>

              <div className="detail-overview">
                <h2>{title}</h2>
                <p>
                  {released} &bull; {runtime}
                </p>
                <p>{genre}</p>
                <p>
                  <span>üåü</span>
                  {imdbRating}  IMDB rating
                </p >
              </div>
            </header>
            {/* <p>{avgRating}</p> */}
            <section className="rating">
              <div className="rating">

                {
                  !isWatched ?
                    <>
                      <StartRating
                        maxRating={10}
                        size={30}
                        key={selectedId}
                        onSetRating={setUserRating}
                      />

                      {userRating &&
                        <button className="btn-add" onClick={handleAdd}>
                          Add to watched list
                        </button>
                      }
                    </> :
                    (
                      <h2>you have watched this movieü•≥,{watchedMovieRate ? `your rate is ${watchedMovieRate}` : "you have not rating yetüëàüèº"}</h2>
                    )
                }
              </div>

              <p><em>{plot}</em></p>
              <p>Starring {actors}</p>
              <p>Directed by {director}</p>
            </section>
          </>
        }
        {/* {selectedId} */}
      </div>

    </>
  );
}

function WatchedBox({ children }) {

  const [isOpen2, setIsOpen2] = useState(true);

  return (
    <div className="box">
      <button
        className="btn-toggle"
        onClick={() => setIsOpen2((open) => !open)}
      >
        {isOpen2 ? "‚Äì" : "+"}
      </button>
      {isOpen2 && (children)}
    </div>
  )
}

function WatchedSummary({ watched }) {

  const avgImdbRating = average(watched.map((movie) => movie.imdbRating.value));
  const avgUserRating = average(watched.map((movie) => movie.userRating));
  const avgRuntime = average(watched.map((movie) => movie.runtime));
  return (
    <div className="summary">
      <h2>Movies you watched</h2>
      <div>
        <p>
          <span>#Ô∏è‚É£</span>
          <span>{watched.length} movies</span>
        </p>
        <p>
          <span>‚≠êÔ∏è</span>
          <span>{avgImdbRating.toFixed(2)}</span>
        </p>
        <p>
          <span>üåü</span>
          <span>{avgUserRating.toFixed(2)}</span>
        </p>
        <p>
          <span>‚è≥</span>
          <span>{avgRuntime.toFixed(2)} min</span>
        </p>
      </div>
    </div>
  )
}

function WatchedMoviesList({ watched ,onDeleteWatched}) {
  return (
    <ul className="list">
      {watched.map((movie) => (
        <WatchedMovie 
          movie={movie}
          onDeleteWatched = {onDeleteWatched}
        />
      ))}
    </ul>
  )
}

function WatchedMovie({ movie,onDeleteWatched }) {
  // console.log(Number(movie.imdbRating));
  return (
    <li key={movie.imdbID}>
      <img src={movie.poster} alt={`${movie.title} poster`} />
      <h3>{movie.title}</h3>
      <div>
        <p>
          <span>‚≠êÔ∏è</span>
          <span>{movie.imdbRating}</span>
        </p>
        <p>
          <span>üåü</span>
          <span>{movie.userRating}</span>
        </p>
        <p>
          <span>‚è≥</span>
          <span>{movie.runtime} min</span>
        </p>

        <button 
          className="btn-delete" 
          onClick={()=>onDeleteWatched(movie.imdbID)}
        >X</button>
      </div>
    </li>
  )
}
